import React from 'react'
import { IMovieCard } from './types'
import { IMAGE_SOURCE } from '../../constants/moviesMock';
import { Pill } from '../Pill';
import genres from '../../constants/genres.json';

const MovieCard: React.FC<IMovieCard> = ({
    title,
    genreId,
    movieId,
    voteAverage,
    posterPath,
}) => {
    const poster = IMAGE_SOURCE + posterPath;

    const getGenre = (genreId: number): string => {
        const genre = genres.find((genre) => genre.id === genreId);
        return genre ? genre.name : '';
    }
  return (
    <div className='bg-white h-auto float-left overflow-hidden block mb-8 mr-7 relative shadow-black rounded-lg p-0 shrink-0 scroll-smooth'>
        <div className='bg-slate-600 min-w-32 h-80 relative float-none'>
            <img className='transition duration-1000 scale-100 overflow-hidden min-w-32 h-80 relative max-w-none ml-0 hover:scale-125 hover:opacity-40' src={poster} alt='poster'/>
        </div>
        <div className='rounded-lg absolute bottom-0 left-0 w-full h-auto opacity-100 transition-all duration-300 bg-gradient-to-t from-black to-transparent'>
            <div className='text-lg font-bold text-white mt-2 mb-1'>
                <div><Pill title={getGenre(genreId)} color={voteAverage < 5 ? 'red' : voteAverage === 5 ? 'yellow' : 'green'}/></div>
                <p>{title}</p>
                <p>*{voteAverage} / 10</p>
            </div>
        </div>
    </div>
  )
}

export default MovieCard
