import React from "react";
import { IPill } from "./types";
import './Pill.css';
import classNames from "classnames";

const Pill: React.FC<IPill> =({
    title,
    color,
}) => {
    return (
        <div className='bg-red-600 text-white rounded-lg px-3 py-1 text-sm font-medium mr-2 inline-flex items-center justify-center;'>
            {title}
        </div>
    )
}

export default Pill;
