import httpInstance from "../httpInstance";

export const getMovieDetails = async () => {
    let res: any;
    let id = window.location.pathname.split('/')[0];
    const endpoint = `${id}?api_key=${process.env.REACT_APP_MDB_AI_API_KEY}&language=en-US`;
    await httpInstance
    .get(endpoint)
    .then((response) => {
        res = response.data;
    })
    .catch((err) => {
        res = err.response
    });
    return res;
}
