import React from "react";
import { MovieCard } from "../../components/MovieCard";
import { movies } from "../../constants/moviesMock";

const Home = () => {
  return (
    <div className="grid grid-cols-1 mx-2">
      <h1 className="text-3xl text-gray-800 font-semibold my-6 mx-12">POPULAR</h1>
        <div className='snap-x overflow-auto flex space-x-1'>
          <div className=""><MovieCard
            title={movies[0].title}
            movieId={movies[0].id}
            posterPath={movies[0].poster_path}
            genreId={movies[0].genre_ids[0]}
            voteAverage={movies[0].vote_average}/></div>
          <div className=""><MovieCard
            title={movies[1].title}
            movieId={movies[1].id}
            posterPath={movies[1].poster_path}
            genreId={movies[1].genre_ids[0]}
            voteAverage={movies[1].vote_average}/></div>
          <div className=""><MovieCard
            title={movies[2].title}
            movieId={movies[2].id}
            posterPath={movies[2].poster_path}
            genreId={movies[2].genre_ids[0]}
            voteAverage={movies[2].vote_average}/></div>
          <div className=""><MovieCard
            title={movies[3].title}
            movieId={movies[3].id}
            posterPath={movies[3].poster_path}
            genreId={movies[3].genre_ids[0]}
            voteAverage={movies[3].vote_average}/></div>
          <div className=""><MovieCard
            title={movies[4].title}
            movieId={movies[4].id}
            posterPath={movies[4].poster_path}
            genreId={movies[4].genre_ids[0]}
            voteAverage={movies[4].vote_average}/></div>
          <div className=""><MovieCard
            title={movies[5].title}
            movieId={movies[5].id}
            posterPath={movies[5].poster_path}
            genreId={movies[5].genre_ids[0]}
            voteAverage={movies[5].vote_average}/></div>
          <div className=""><MovieCard
            title={movies[6].title}
            movieId={movies[6].id}
            posterPath={movies[6].poster_path}
            genreId={movies[6].genre_ids[0]}
            voteAverage={movies[6].vote_average}/></div>
          <div className=""><MovieCard
            title={movies[7].title}
            movieId={movies[7].id}
            posterPath={movies[7].poster_path}
            genreId={movies[7].genre_ids[0]}
            voteAverage={movies[7].vote_average}/></div>
        </div>
      </div>   
  );
}

export default Home;
