import React from 'react';
import { IMovieCard } from './types';
import { IMAGE_SOURCE } from '../../constants/moviesMock';
import { Pill } from '../Pill';
import genres from '../../constants/genres.json';

interface Genre {
  id: number;
  name: string;
}

const MovieCard: React.FC<IMovieCard> = ({
  title,
  genreId,
  movieId,
  voteAverage,
  posterPath,
}) => {
  const poster = IMAGE_SOURCE + posterPath;

  const getGenre = (genreId: number): string => {
    const genre: Genre | undefined = genres.genres.find(
      (genre: Genre) => genre.id === genreId
    );
    return genre ? genre.name : genreId.toString();
  };

  return (
    <div className='bg-white w-64  h-96 float-left overflow-hidden block mx-3 mb-8 mr-7 relative shadow-black rounded-lg p-0 shrink-0 scroll-smooth'>
      <div className='bg-slate-600 w-64  h-96 relative float-none'>
        <img
          className='transition duration-1000 scale-100 overflow-hidden w-64 h-96 relative max-w-none ml-0 hover:scale-125 hover:opacity-40'
          src={poster}
          alt='poster'
        />
      </div>
      <div className='rounded-lg absolute bottom-0 left-0 w-full h-auto opacity-100 transition-all duration-300 bg-gradient-to-t from-black to-transparent'>
        <div className=' text-white mx-2 mb-5'>
          <div>
            <Pill
              title={getGenre(genreId)}
              color='red'
            />
          </div>
          <div className='text-lg font-bold'>{title}</div>
          <div className='text-xs'>⭐︎★☆{voteAverage} / 10</div>
        </div>
      </div>
    </div>
  );
};

export default MovieCard;
