import React from "react";
import { IPill } from "./types";
import classNames from "classnames";

const Pill: React.FC<IPill> =({
    title,
    color,
}) => {
    const pillClass = classNames({
        'text-white text-xs font-normal text-center p-1.5 rounded': true,

        'bg-red-500 text-white': color === 'red',
        'bg-green-600 text-white': color === 'green',
        'bg-yellow-600 text-white': color === 'yellow',
    })
    return (
        <div className={pillClass}>{title}</div>
    )
}

export default Pill;
