import React, { useEffect, useState } from 'react';
import {useLocation, useParams, useNavigate} from 'react-router-dom';
import { getMovieDetails } from '../../services/movies/getMovieDetails';
import { getMovieRecommendations } from '../../services/movies/getMovieRecommendations';
import { MovieCard } from '../../components/MovieCard';
import { get } from 'http';
import { IMovieResponse } from '../../services/movies/types';
import {Group, Time, Calendar, Star, Graph} from '../../assets';
import classNames from 'classnames';

const Show = () => {
    const {id} = useParams();
    const location = useLocation();
    const navigate = useNavigate();

    const [details, setDetails] = useState<IMovieResponse>();
    const [loading, setLoading] = useState<boolean>(false);
    const [errorDetails, setErrorDetails] = useState<boolean>(false);

    const [recommendations, setRecommendations] = useState<any[]>([]);
    const [loadingRecommendations, setLoadingRecommendations] = useState<boolean>(false);
    const [errorRecommendations, setErrorRecommendations] = useState<boolean>(false);

    const goBack = () => {
        navigate(-1);
    };

    const getDetails = async () => {
        await getMovieDetails()
        .then((res) => {
            setDetails(res);
        }) 
        .catch((err) => {
          setErrorDetails(true);
        });
        setLoading(false);
    };

    const getRecommendations = async () => {
        await getMovieRecommendations()
        .then((res) => {
          if (res && res.data) {
            setRecommendations(res.data.results);
          }
        }) 
        .catch((err) => {
          setErrorRecommendations(true);
        });
        setLoading(false);
    };

    useEffect(() => {
        setLoading(true);
        getDetails();
        getRecommendations(); 
    }, []);

    return (
        <div>
            <div className='flex'>
                <div className='h-fit w-72 overflow-auto shadow-xl rounded-lg mx-3 my-3'><img src={location.state.poster} alt='poster'></img></div>
                <div className='grid grid-rows-4 -space-y-15'>
                    <div className='text-4xl text-gray-800 font-semibold mx-4 my-10'>{location.state.title}</div>
                    <div className='flex'>
                        <div className='text-md font-medium'>
                            <div className='flex'>
                                <img src={Group}></img>
                                <div className='mx-2'>{details?.adult == true && '18+'}{details?.adult == false && '18-'}</div>
                            </div>
                        </div>
                        <div className='text-md font-medium mx-4'>{details?.release_date}</div>
                        <div className='text-md font-medium mx-4'>{details?.runtime} min.</div>
                        <div className='text-md font-medium mx-4'>{details?.vote_average}</div>
                        <div className='text-md font-medium mx-4'>{details?.vote_count}</div>
                    </div>
                    <div className=' text-sm font-light mx-4 -my-16'>"{details?.tagline}"</div>
                    <div className=' text-sm font-light mx-4 -my-40'>{details?.overview}</div>

                </div>
            </div>
            <div className='text-3xl text-gray-800 font-medium mx-10 -my-1'>RECOMMENDATIONS</div>
            {loading && <div>Loading...</div>}
            {errorDetails && <div>Error...</div>}

            <button onClick={goBack}>Ir atr√°s</button>
        </div>
    );
};

export default Show;
