import React, {useEffect, useState} from "react";
import { MovieCard } from "../../components/MovieCard";
import { movies } from "../../constants/moviesMock";
import { getPopularMovies } from "../../services/movies/getPopularMovies";

const Popular = () => {
  const [movies, setMovies] = useState<any[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [errorMovies, setErorMovies] = useState<boolean>(false);

  const getPopular = async () => {
    await getPopularMovies()
    .then((res) => {
      if (res && res.data) {
        console.log(res.data.results, "res");
        setMovies(res.data.results);
      }
    }) 
    .catch((err) => {
      setErorMovies(true);
    });
    setLoading(false);
  };

  useEffect(() => {
    setLoading(true);
    getPopular();
  }, []);

  return (
    <div className="bg-white h-20 shadow-md">
      <div className="flex mr-24">
        <div className="flex-1 w-1/2 text-2xl  text-gray-600 font-bold mx-5 my-6">Movies DB</div>
        <div className="flex-none w-20 text-sm font-medium text-rose-600 my-7"><Link to={ROUTES.HOME}>HOME</Link></div>
        <div className="flex-none w-24 text-sm font-medium hover:text-rose-600 my-7"><Link to={ROUTES.POPULAR}>POPULAR</Link></div>
        <div className="flex-none w-28 text-sm font-medium hover:text-rose-600 my-7"><Link to={ROUTES.TOP_RATED}>TOP RATED</Link></div>
        <div className="flex-none w-28 text-sm font-medium hover:text-rose-600 my-7"><Link to={ROUTES.NOW_PLAYING}>NOW PLAYING</Link></div>
        <div className="flex-none w-28 text-sm font-medium hover:text-rose-600 my-7"><Link to={ROUTES.MY_FAVORITES}>MY FAVORITES</Link></div>
      </div>
      <div>
      {loading && <div>Loading...</div>}
      {errorMovies && <div>Error...</div>}
      {movies?.length > 0 && 
        movies.map((movie) => (
          <MovieCard
            key={movie.id}
            movieId={movie.id}
            posterPath={movie.poster_path}
            title={movie.title}
            genreId={movie.genre_ids[0]}
            voteAverage={movie.vote_average}
          />
        ))
      }
    </div>
    </div>
  );
};

export default Popular;
