import React, { useEffect, useState } from 'react';
import {useLocation, useParams, useNavigate} from 'react-router-dom';
import { getMovieDetails } from '../../services/movies/getMovieDetails';
import { getMovieRecommendations } from '../../services/movies/getMovieRecommendations';
import { MovieCard } from '../../components/MovieCard';
import { get } from 'http';
import { IMovieResponse } from '../../services/movies/types';
import {Group, Time, Calendar, Star, Graph} from '../../assets';
import classNames from 'classnames';
import { Pill } from '../../components/Pill';

const Show = () => {
    const {id} = useParams();
    const location = useLocation();
    const navigate = useNavigate();

    const [details, setDetails] = useState<IMovieResponse>();
    const [loading, setLoading] = useState<boolean>(false);
    const [errorDetails, setErrorDetails] = useState<boolean>(false);

    const [recommendations, setRecommendations] = useState<any[]>([]);
    const [loadingRecommendations, setLoadingRecommendations] = useState<boolean>(false);
    const [errorRecommendations, setErrorRecommendations] = useState<boolean>(false);

    const goBack = () => {
        navigate(-1);
    };

    const getDetails = async () => {
        await getMovieDetails()
        .then((res) => {
            setDetails(res);
        }) 
        .catch((err) => {
          setErrorDetails(true);
        });
        setLoading(false);
    };

    const getRecommendations = async () => {
        await getMovieRecommendations()
        .then((res) => {
          if (res && res.data) {
            setRecommendations(res.data.results);
          }
        }) 
        .catch((err) => {
          setErrorRecommendations(true);
        });
        setLoading(false);
    };

    useEffect(() => {
        setLoading(true);
        getDetails();
        getRecommendations(); 
    }, []);

    return (
        <div>
            <div className='flex'>
                <div className='h-fit w-72 overflow-auto shadow-xl rounded-lg mx-3 my-3'><img src={location.state.poster} alt='poster'></img></div>
                <div className='grid'>
                    <div className='text-4xl text-gray-800 font-semibold mx-4 my-10'>{location.state.title}</div>
                    <div className='flex -my-5'>
                        <div className='text-md font-medium mx-4'>
                            <div className='flex'>
                                <img src={Group}></img>
                                <div className='mx-1'>{details?.adult == true && '18+'}{details?.adult == false && '18-'}</div>
                            </div>
                        </div>
                        <div className='text-md font-medium mx-4'>
                            <div className='flex'>
                                <img src={Time}></img>
                                <div className='text-md font-medium'>{details?.runtime} min.</div>
                            </div>
                        </div>
                        <div className='text-md font-medium mx-4'>
                            <div className='flex'>
                                <img src={Calendar}></img>
                                <div className='mx-1'>{details?.release_date}</div>
                            </div>
                        </div>
                        <div className='text-md font-medium mx-4'>
                            <div className='flex'>
                                <img src={Star}></img>
                                <div className='text-md font-medium'>{details?.vote_average}</div>
                            </div>
                        </div>
                        <div className='text-md font-medium mx-4'>
                            <div className='flex'>
                                <img src={Graph}></img>
                                <div className='text-md font-medium'>{details?.vote_count}</div>
                            </div>
                        </div>
                    </div>
                    <div className=' h-auto text-sm font-light mx-4'>"{details?.tagline}"</div>
                    <div className=' h-auto text-sm font-light mx-4 -my-10'>{details?.overview}</div>
                    <div className='flex w-auto'>
                        <div className='grid my-5'>
                            <div className='text-lg font-semibold mx-4'>Genres</div>
                            <div className='flex space-x-2 mx-4'>
                                {details?.genres && details?.genres.map((genre) => (
                                <Pill title={genre.name} color={'red'}></Pill>))}
                            </div>
                        </div>
                        <div className='grid my-5'>
                            <div className='text-lg font-semibold mx-4'>Favorite</div>
                            <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold p-2 rounded-lg">
                                Add to favorites
                            </button>
                        </div>
                    </div>
                </div>
            </div>
            <div className='text-3xl text-gray-800 font-medium mx-10 -my-1'>RECOMMENDATIONS</div>
            {loading && <div>Loading...</div>}
            {errorDetails && <div>Error...</div>}

            <button onClick={goBack}>Ir atr√°s</button>
        </div>
    );
};

export default Show;
