import React from "react";
import { MovieCard } from "../../components/MovieCard";
import { movies } from "../../constants/moviesMock";

const Home = () => {
  return (
    <div className="grid grid-flow-row-dense grid-cols-4">
      <div className="ltr">
        <div className='snap-x' style={{ overflowX: 'auto', scrollbarWidth: 'none', WebkitOverflowScrolling: 'touch' }}>
          <div className="scroll-ms-6 snap-start" style={{ scrollSnapType: 'x mandatory', display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '1rem', padding: '1rem', MsOverflowStyle: 'none', scrollbarWidth: 'none', WebkitScrollbar: 'none', WebkitOverflowScrolling: 'touch' }}>
            {movies.map((movie, index) => (
              <div key={index} className="snap-start" style={{ scrollSnapAlign: 'start' }}>
                <MovieCard
                  title={movie.title}
                  movieId={movie.id}
                  posterPath={movie.poster_path}
                  genreId={movie.genre_ids[0]}
                  voteAverage={movie.vote_average}
                />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
